actions.script:
import org.apache.commons.codec.digest.DigestUtils
import org.moqui.impl.context.ContextJavaUtil

def hash = { listOrMap ->
    if (listOrMap instanceof Map) {
        return DigestUtils.sha256Hex(ContextJavaUtil.jacksonMapper.writeValueAsString(listOrMap.sort()))
    } else if (listOrMap instanceof List) {
        return DigestUtils.sha256Hex(listOrMap.collect { it.toString() }.sort().join("||"))
    } else {
        return DigestUtils.sha256Hex(listOrMap?.toString() ?: "")
    }
}

if.script:
def oldSet = new HashSet((history?.metafields ?: []) as List)
def newVal = "${productJson.key}/${productJson.namespace}/${productJson.value}"
if (!oldSet.contains(newVal)) {
diffMap.metafields = [added: [newVal], removed: []]
def newMetafields = (oldSet + newVal) as List
newMetafields = newMetafields.collect { it.toString() }.sort()
}

// core product hash
def parts = [
    productJson.handle ?: '',
    productJson.vendor ?: '',
    (productJson.hasVariantsThatRequiresComponents ?: '').toString(),
    (productJson.isGiftCard ?: '').toString(),
    productJson.title ?: '',
    (productJson.featuredMedia?.preview?.image?.url ?: '')
]
def productCoreDetailsHash = hash(parts.join('||'))

// tags as List
def tagsRaw = productJson.tags
def tags = []

if (tagsRaw instanceof String) {
    tags = tagsRaw.split(',')*.trim().findAll { it }
} else if (tagsRaw instanceof List) {
    tags = tagsRaw.collect { it.toString().trim() }.findAll { it }
}
tags = tags.sort()
def tagsHash = hash(tags)

// features as List of position/name/value
def features = []
def sequence = 1
(productJson.options ?: []).each { opt ->
    def position = opt.position
    def name = opt.name
    (opt.optionValues ?: []).each { val ->
        features << "${sequence++}/${position}/${name}/${val.name}"
    }
}
features = features.collect { it.toString() }.sort { a, b ->
    def sequenceNumberA = a.split('/')[0].toInteger()
    def sequenceNumberB = b.split('/')[0].toInteger()
    return sequenceNumberA <=> sequenceNumberB
}
def featuresHash = hash(features)

// identifications map
def identifications = [id: productJson.id]
def identificationsHash = hash(identifications)

// Build initial diffMap with added lists (so consumer sees same schema)
def diffMap = [
    id: productJson.id,
    handle: productJson.handle,
    vendor: productJson.vendor,
    title: productJson.title,
    featureImageUrl: productJson.featuredMedia?.preview?.image?.url,
    isGiftCard: productJson.isGiftCard,
    hasVariantsThatRequiresComponents: productJson.hasVariantsThatRequiresComponents,
    tags: [added: tags, removed: []],
    features: [added: features, removed: []],
    identifications: identifications
]

// prepare JSON-serialized fields for storage (so DB has JSON text)
tagsJson = ContextJavaUtil.jacksonMapper.writeValueAsString(tags)
featuresJson = ContextJavaUtil.jacksonMapper.writeValueAsString(features)
identificationsJson = ContextJavaUtil.jacksonMapper.writeValueAsString(identifications)
diffMapJson = ContextJavaUtil.jacksonMapper.writeValueAsString(diffMap)
featuresHash = featuresHash
identificationsHash = identificationsHash

actions.script:
// --- Core Product Hash ---
def parts = [
    productJson.handle ?: '',
    productJson.vendor ?: '',
    (productJson.hasVariantsThatRequiresComponents ?: '').toString(),
    (productJson.isGiftCard ?: '').toString(),
    productJson.title ?: '',
    (productJson.featuredMedia?.preview?.image?.url ?: '')
]
ec.logger.info("\n\n --${productId} --${productJson}+++\n\n-- ${productJson.featuredMedia?.preview?.image?.url}----------------------- \n\n");
def newCoreHash = hash(parts.join('||'))
if (newCoreHash != productUpdateHistory.productCoreDetailsHash) {
    differenceMap.productCoreDetailsHash = newCoreHash
    differenceMap.title = productJson.title
    differenceMap.vendor = productJson.vendor
    differenceMap.handle = productJson.handle
    differenceMap.isGiftCard = productJson.isGiftCard
    differenceMap.hasVariantsThatRequireComponents = productJson.hasVariantsThatRequiresComponents
    differenceMap.featureImageUrl = productJson.featuredMedia?.preview?.image?.url
}

// --- Tags ---
def newTagsRaw = productJson.tags
def newTags = []
if (newTagsRaw instanceof String) {
    newTags = newTagsRaw.split(',')*.trim().findAll { it }
} else if (newTagsRaw instanceof List) {
    newTags = newTagsRaw.collect { it.toString().trim() }.findAll { it }
}
newTags = newTags.sort()
def oldTags = ContextJavaUtil.jacksonMapper.readValue(productUpdateHistory.tags ?: "[]", List.class)
def addedTags = (newTags - oldTags).collect { it.toString() }
def removedTags = (oldTags - newTags).collect { it.toString() }
if (addedTags || removedTags) {
    differenceMap.tags = [added: addedTags, removed: removedTags]
}
def newTagsHash = hash(newTags)

// --- Features ---
def newFeatures = []
def sequence = 1
(productJson.options ?: []).each { opt ->
    def pos = opt.position
    def name = opt.name
    (opt.optionValues ?: []).each { val ->
        newFeatures << "${sequence++}/${pos}/${name}/${val.name}"
    }
}
newFeatures = newFeatures.collect { it.toString() }.sort { a, b ->
    def sequenceNumberA = a.split('/')[0].toInteger()
    def sequenceNumberB = b.split('/')[0].toInteger()
    return sequenceNumberA <=> sequenceNumberB
}
def oldFeatures = ContextJavaUtil.jacksonMapper.readValue(productUpdateHistory.features ?: "[]", List.class)
def addedFeatures = (newFeatures - oldFeatures).collect { it.toString() }
def removedFeatures = (oldFeatures - newFeatures).collect { it.toString() }
if (addedFeatures || removedFeatures) {
    differenceMap.features = [added: addedFeatures, removed: removedFeatures]
}
def newFeaturesHash = hash(newFeatures)

// --- Identifications ---
def newIdent = [id: productJson.id]
def oldIdent = ContextJavaUtil.jacksonMapper.readValue(productUpdateHistory.identifications ?: "{}", Map.class)
if (!oldIdent.equals(newIdent)) {
    differenceMap.identifications = newIdent
}
def identificationsHash = hash(newIdent)

// --- Persist if changed ---
if (!differenceMap.isEmpty()) {
    // prepare JSON serialized fields for storage
    updateMap = [
        productId: virtualProductId,
        shopId: shopId,
        systemMessageId: systemMessageId,
        productCoreDetailsHash: newCoreHash,
        features: ContextJavaUtil.jacksonMapper.writeValueAsString(newFeatures),
        featuresHash: newFeaturesHash,
        tags: ContextJavaUtil.jacksonMapper.writeValueAsString(newTags),
        tagsHash: newTagsHash,
        identifications: ContextJavaUtil.jacksonMapper.writeValueAsString(newIdent),
        identificationsHash: identificationsHash,
        differenceMap: ContextJavaUtil.jacksonMapper.writeValueAsString(differenceMap)
    ]
}

import org.apache.commons.codec.digest.DigestUtils
import org.moqui.impl.context.ContextJavaUtil

def hash = { listOrMap ->
    if (listOrMap instanceof Map) {
        return DigestUtils.sha256Hex(ContextJavaUtil.jacksonMapper.writeValueAsString(listOrMap.sort()))
    } else if (listOrMap instanceof List) {
        return DigestUtils.sha256Hex(listOrMap.collect { it.toString() }.sort().join("||"))
    } else {
        return DigestUtils.sha256Hex(listOrMap?.toString() ?: "")
    }
}

diffMap.inventoryItemId = (variantJson.inventoryItem?.id ?: '').toString()

// --- Identifications ---
def newIdent = [sku: variantJson.sku ?: '', barcode: variantJson.barcode ?: '', id: variantJson.id ?: '']
def oldIdent = ContextJavaUtil.jacksonMapper.readValue(productUpdateHistory.identifications ?: "{}", Map.class)
if (!oldIdent.equals(newIdent)) {
    diffMap.identifications = newIdent
}
def identHash = hash(newIdent)

// --- Price ---
def newPrice = variantJson?.price
if (newPrice != productUpdateHistory.price) {
    diffMap.price = newPrice
}

// --- Assocs ---
def newAssoc = co.hotwax.sob.shopify.ShopifyHelper.resolveShopifyGid(variantJson?.product?.id?.toString())
if (newAssoc != productUpdateHistory.parentProductId) {
    diffMap.parentProductId = newAssoc
}

// --- Features ---
def newFeatures = (variantJson.selectedOptions ?: []).collect { "${it.name}/${it.value}" }.sort()
def oldFeatures = ContextJavaUtil.jacksonMapper.readValue(productUpdateHistory.features ?: "[]", List.class)
def addedFeatures = (newFeatures - oldFeatures)
def removedFeatures = (oldFeatures - newFeatures)
if (addedFeatures || removedFeatures) {
    diffMap.features = [:]
    if (addedFeatures) diffMap.features.added = addedFeatures
    if (removedFeatures) diffMap.features.removed = removedFeatures
}
def featuresHash = hash(newFeatures)

// --- Tags ---
def newTags = (variantJson.product?.productType ? [variantJson.product.productType.toString()] : []).sort()
def oldTags = ContextJavaUtil.jacksonMapper.readValue(productUpdateHistory.tags ?: "[]", List.class)
def addedTags = (newTags - oldTags)
def removedTags = (oldTags - newTags)
if (addedTags || removedTags) {
    diffMap.tags = [added: addedTags, removed: removedTags]
}
def tagsHash = hash(newTags)

// --- Core details hash ---
def parts = [
    (variantJson.position ?: '').toString(),
    variantJson.title ?: '',
    (variantJson.image?.url ?: ''),
    (variantJson.requiresComponents ?: '').toString(),
    (variantJson.product?.isGiftCard ?: '').toString(),
    (variantJson.inventoryItem?.requiresShipping ?: '').toString(),
    (variantJson.inventoryItem?.id ?: '').toString(),
    (variantJson.inventoryItem?.measurement?.weight?.unit ?: '').toString(),
    (variantJson.inventoryItem?.measurement?.weight?.value ?: '1').toString()
]
def newCoreHash = hash(parts.join('||'))
if (newCoreHash != productUpdateHistory.productCoreDetailsHash) {
    diffMap.productCoreDetailsHash = newCoreHash
    diffMap.position = variantJson.position
    diffMap.title = variantJson.title
    diffMap.requiresShipping = (variantJson.inventoryItem?.requiresShipping ?: '').toString()
    diffMap.requiresComponents = (variantJson.requiresComponents ?: '').toString()
    diffMap.isGiftCard = (variantJson.product?.isGiftCard ?: '').toString()
    diffMap.featureImageUrl = (variantJson.image?.url ?: '')
    diffMap.weightUnit = (variantJson.inventoryItem?.measurement?.weight?.unit ?: '').toString()
    diffMap.weightValue = (variantJson.inventoryItem?.measurement?.weight?.value ?: '1').toString()
}

// --- Persist updates ---
if (!diffMap.isEmpty()) {
    ec.service.sync().name("update#co.hotwax.product.ProductUpdateHistory")
      .parameters([
        productId: variantProductId,
        shopId: shopId,
        systemMessageId: systemMessageId,
        productCoreDetailsHash: newCoreHash,
        features: ContextJavaUtil.jacksonMapper.writeValueAsString(newFeatures),
        featuresHash: featuresHash,
        tags: ContextJavaUtil.jacksonMapper.writeValueAsString(newTags),
        tagsHash: tagsHash,
        identifications: ContextJavaUtil.jacksonMapper.writeValueAsString(newIdent),
        identificationsHash: identHash,
        differenceMap: ContextJavaUtil.jacksonMapper.writeValueAsString(diffMap)
      ])
      .requireNewTransaction(true)
      .call()
}
